mapper.select:

select no, id, name, email, gender, birthDate
  from member
 where id = #{id }
   and password = #{password }


select  no, id, name, email, gender, birthDate
  from member
 where no = #{no }


select  no, id, name, email, gender, birthDate
  from member
 where id = #{id }


select  id, password, name 
  from member
 where id = #{id } and password = #{password }
